import { Component, AfterViewChecked, ChangeDetectionStrategy, NgZone } from "@angular/core";
import { init, errorCodes } from "../assets/anylinejs/anyline.js";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent implements AfterViewChecked {
  anylicense =
    "";

  anyline;
  constructor(
    private zone: NgZone

  ) {}

  ngAfterViewChecked() {
    // console.log('ChangeDetectionCycle is ran');
  }

/*   startScanOutsideZone() {   //even if the scanning is run outside of ngZone and no changeDetection cycles are run there is still some process going on in the background
    this.zone.runOutsideAngular(() =>
    this.startScan()
    );
  } */

  stop() {
    this.anyline.stopScanning();
    this.anyline.dispose();
    this.anyline = null;
    document.getElementById("anylineContainer").remove();
  }

  startScan() {
    const viewConfig = {
      outerColor: "000000",
      outerAlpha: 0.5,
      cutouts: [
        {
          cutoutConfig: {
            style: "rect",
            maxWidthPercent: "70%",
            alignment: "top_half",
            ratioFromSize: {
              width: 62,
              height: 9,
            },
            strokeWidth: 2,
            strokeColor: "FFFFFF",
            cornerRadius: 4,
            outerColor: "000000",
            outerAlpha: 0.3,
            feedbackStrokeColor: "0099FF",
          },
          scanFeedback: {
            animation: "traverse_multi",
            animationDuration: 250,
            style: "contour_rect",
            strokeWidth: 2,
            strokeColor: "0099FF",
            fillColor: "220099FF",
          },
        },
      ],
    };

    const container = document.getElementById("anylineContainer");

    this.anyline = init({
      preset: "vin",
      viewConfig,
      license: this.anylicense,
      element: container,
      debugAnyline: true,
      anylinePath: "./assets/anylinejs",
    });

    this.anyline.startScanning();

    this.anyline.onResult = ({ image, fullImage, result }) => {
      console.log("result: ", result);

    };

    this.anyline.onReport = (report) => {
      console.log("anyLine Reports: ", report);
    };

    this.anyline.onError = ({ code, message }) => {
      if (code === errorCodes.WEBCAM_ERROR) {
        console.error("webcam error: ", message);
      }
    };

    this.anyline.onLoad = () => {
      console.log("Anyline Finished Loading");
    };
  }
}
